import styled from "styled-components";

export const WapperAnimation = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;
export const ContainerAnimation = styled.div`
  width: 100%;
  max-width: 895px;
  aspect-ratio: 895/657;
  /* border: 1px solid #fff; */
  display: flex;
  align-items: center;
  justify-content: center;
  perspective: 300px;
  z-index: 20;
`;

export const ContainerFloor = styled.div`
  width: 100%;
  height: 100%;
  transform-style: preserve-3d;
  transform: rotateX(90deg);
`;
export const ContainerMain = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  transform-style: preserve-3d;
  transform: rotateX(-90deg) translateZ(-0px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 5;
`;
export const Orbit1 = styled.div`
  width: 80%;
  aspect-ratio: 1;
  position: absolute;
  border-radius: 50%;
  transform-style: preserve-3d;
  transform: translateZ(-200px) translateY(-25%) translateX(20%) rotateX(21deg)
    rotateZ(-15deg);
  display: flex;
  align-items: center;
  justify-content: center;
  border: 2px solid #763CAC;
  animation: RotateHome1 10s ease-in-out infinite ;
  @keyframes RotateHome1 {
    0% {
      transform: translateZ(-250px) translateY(-25%) translateX(20%) rotateX(21deg)
    rotateZ(0deg);
    }
    100% {
      transform: translateZ(-250px) translateY(-25%) translateX(20%) rotateX(21deg)
    rotateZ(360deg);
    }
  }
`;
